exports.evenAndOdd = function(playerChoice){
    var casualNumber = Math.floor(Math.random() * 150)+1;
    if (playerChoice === 'even') {
         if (casualNumber%2 == 0){
             return 'You win!';
         }
         else {
             return 'You lose!';
         }
    } 
    else if (playerChoice === 'odd'){
        if (casualNumber%2 == 1){
            return 'You Win!'
        }
        else {
            return 'You lose!'
        }
    }

}
